
public class 관계연산자2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b=9;
		boolean result=(a<b);
		System.out.println("result="+result);
		result=(a<=b);
		System.out.println("result="+result);
		result=(a>b);
		System.out.println("result="+result);
		result=(a>=b);
		System.out.println("result="+result);
	}

}
