// trim() : 좌우 공백 제거
public class 문자열6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String id=" admin ";
		System.out.println(id);
		System.out.println("문자 길이: "+id.length());
		String res=id.trim();
		System.out.println("문자 길이: "+res.length());
		System.out.println(res);
	}

}
